const FilterType = props => {
    const { iconClass } = props;
    switch (props.type) {
        case 'VI':
            return <Visa className={iconClass} />;
            break;
        case 'MC':
            return <MasterCard className={iconClass} />;
            break;
        case 'AE':
            return <AmericanExpress className={iconClass} />;
            break;
        case 'DN':
            return <Diners className={iconClass} />;
            break;
        case 'ELO':
            return <Elo className={iconClass} />;
            break;
        case 'AU':
            return <Aura className={iconClass} />;
            break;
        case 'HC':
            return <HyperCard className={iconClass} />;
            break;

        default:
            return null;
            break;
    }
};

const CcTypes = props => {
    const { availabeTypes, filterBy } = props;
    const classes = mergeClasses(defaultClasses, props.classes);

    if (filterBy) {
        return (
            <div className={classes.filterBy}>
                <FilterType iconClass={classes.availableIcon} type={filterBy} />
            </div>
        );
    }

    if (availabeTypes.lenght === 0) {
        return null;
    }
    return (
        <div className={classes.root}>
            {availabeTypes.map(type => {
                if (type === 'AE') {
                    return (
                        <AmericanExpress
                            key={type}
                            className={classes.availableIcon}
                        />
                    );
                }

                if (type === 'VI') {
                    return (
                        <Visa key={type} className={classes.availableIcon} />
                    );
                }

                if (type === 'MC') {
                    return (
                        <MasterCard
                            key={type}
                            className={classes.availableIcon}
                        />
                    );
                }

                if (type === 'DN') {
                    return (
                        <Diners key={type} className={classes.availableIcon} />
                    );
                }

                if (type === 'HC') {
                    return (
                        <HyperCard
                            key={type}
                            className={classes.availableIcon}
                        />
                    );
                }

                if (type === 'ELO') {
                    return <Elo key={type} className={classes.availableIcon} />;
                }
            })}
        </div>
    );
};

CcTypes.propTypes = {
    classes: shape({
        root: string,
        filterBy: string,
        availableIcon: string
    }),
    availableTypes: arrayOf(string),
    filterBy: string
};

CcTypes.defaultProps = {};
export default CcTypes;
